const aesEncrypt = (word) => {
    eval(getCryptoJS());
    let str_key = CryptoJS.enc.Utf8.parse("0CoJUm6Qyw8W8jud");
    let str_iv = CryptoJS.enc.Utf8.parse('0102030405060708');
    let aes_str = CryptoJS.AES.encrypt(word, str_key, {
        iv: str_iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
    }).toString();
    str_key = CryptoJS.enc.Utf8.parse("aaaabbbbccccdddd");
    return CryptoJS.AES.encrypt(aes_str, str_key, {
        iv: str_iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
    }).toString();
}

