                    mid = input.match(/(playlist\/|id=)(\d+)/)[2]
                }
                NetEaseMap = '/api/v6/playlist/detail?id=' + mid + '&n=1000&limit=1000'
                NetEaseMap = NetEaseConfig(NetEaseMap, '', 'GET').playlist
                Break = true
                NetEase.forEach(item => {
                    if (item.mid == NetEaseMap.id) {
                        toast('歌单重复导入了哦')
                        Break = false
                    }
                })
                if (Break) {
                    NetEase.push({
                        name: NetEaseMap.name,
                        picUrl: NetEaseMap.coverImgUrl,
                        mid: NetEaseMap.id
                    })
                    saveFile(down, JSON.stringify(dataMap), 0);
                    refreshPage(false)
                }
                break;
            case MusicMap[1]:
                if (input.startsWith(MusicMap[1])) {
                    mid = input.match(/识别：(.*)/)[1]
                    mid = base64Decode(mid)
                } else if (input.search('qq.com') >= 1) {
                    mid = input.match(/id=(\d+)/)[1]
                }
                TencentMap = TencentConfig(Number(mid)).Songlist.data.dirinfo
                Break = true
                Tencent.forEach(item => {
                    if (item.mid == TencentMap.id) {
                        toast('歌单重复导入了哦')
                        Break = false
                    }
                })
                if (Break) {
                    Tencent.push({
                        name: TencentMap.title,
                        picUrl: TencentMap.picurl,
                        mid: TencentMap.id
                    })
                    saveFile(down, JSON.stringify(dataMap), 0);
                    refreshPage(false)
                }
                break;
            case MusicMap[2]:
                if (input.startsWith(MusicMap[2])) {
                    mid = input.match(/识别：(.*)/)[1]
                    mid = base64Decode(mid)
                } else if (input.search('kuwo.cn') >= 1) {
                    mid = input.match(/(playlist\/|playlist_detail\/)(\d+)/)[2]
                }
                KwmusiccarUrl = '/playlist/' + mid + '?pn=1&rn=20&ua=&ip='
                KwmusiccarMap = KwmusiccarConfig(KwmusiccarUrl)
                Break = true
                Kwmusiccar.forEach(item => {
                    if (item.mid == KwmusiccarMap.id) {
                        toast('歌单重复导入了哦')
                        Break = false
                    }
                })
                if (Break) {
                    Kwmusiccar.push({
                        name: KwmusiccarMap.name,
                        picUrl: KwmusiccarMap.pic,
                        mid: KwmusiccarMap.id
                    })
                    saveFile(down, JSON.stringify(dataMap), 0);
                    refreshPage(false)
                }
                break;
            case MusicMap[3]:
                try {
                    if (input.startsWith(MusicMap[3])) {
                        Input = input.match(/识别：(.*)/)[1]
                        Input = base64Decode(Input)
                        mid = pdfh(request(Input), 'body&&script,2&&Html')
                        mid = mid.match(/window\.\$output = ([\S\s].*);/)[1]
                    } else if (input.search('kugou.com') >= 1) {
                        Input = input.match(/(https|http)(.*link)/)[0]
                        mid = pdfh(request(Input), 'body&&script,2&&Html')
                        mid = mid.match(/window\.\$output = ([\S\s].*);/)[1]
                    }
                    KugouMap = JSON.parse(mid).info.listinfo
                    Break = true
                    Kugou.forEach(item => {
                        if (item.mid == Input) {
                            toast('歌单重复导入了哦')
                            Break = false
                        }
                    })
                    if (Break) {
                        Kugou.push({
                            name: KugouMap.name,
                            picUrl: KugouMap.pic.replace('{size}', '400'),
                            mid: Input
                        })
                        saveFile(down, JSON.stringify(dataMap), 0);
                        refreshPage(false)
                    }
                } catch (e) {
                    if (input.startsWith(MusicMap[3])) {
                        mid = input.match(/识别：(.*)/)[1]
                        mid = base64Decode(mid)
                    } else if (input.search('kugou.com') >= 1) {
                        mid = input.match(/(\/list\/)(.*)\//)[2]
                    }
                    KugouUrl = 'https://m3ws.kugou.com/plist/list/' + mid
                    KugouMap = request(KugouUrl)
                    Break = true
                    Kugou.forEach(item => {
                        if (item.mid == mid) {
                            toast('歌单重复导入了哦')
                            Break = false
                        }
                    })
                    if (Break) {
                        Kugou.push({
                            name: pdfh(KugouMap, '.content&&p&&Text'),
                            picUrl: pdfh(KugouMap, '.content&&img&&_src'),
                            mid: mid
                        })
                        saveFile(down, JSON.stringify(dataMap), 0);
                        refreshPage(false)
                    }
                }
                break;
            default:
                if (input.startsWith(MusicMap[4])) {
                    mid = input.match(/识别：(.*)/)[1]
                    mid = base64Decode(mid)
                } else if (input.search('migu.cn') >= 1) {
                    if (input.startsWith('分享')) {
                        mid = input.match(/(https|http).*(\w)/)[0]
                        mid = JSON.parse(request(mid, {
                            onlyHeaders: true
                        })).url
                        mid = mid.match(/(id=)(\d+)/)[2]
                    } else {
                        mid = input.match(/(playlist\/)(\d+)/)[2]
                    }
                }
                MiguUrl = 'MIGUM3.0/resource/playlist/v2.0?playlistId=' + mid
                MiguMap = MiguConfig(MiguUrl).data
                Break = true
                Migu.forEach(item => {
                    if (item.mid == MiguMap.musicListId) {
                        toast('歌单重复导入了哦')
                        Break = false
                    }
                })
                if (Break) {
                    Migu.push({
                        name: MiguMap.title,
                        picUrl: MiguMap.imgItem.img,
                        mid: MiguMap.musicListId
                    })
                    saveFile(down, JSON.stringify(dataMap), 0);
                    refreshPage(false)
                }
                break;
        }
    } catch (e) {
        toast('糟糕崩溃了，请检查链接是否有误')
    }
}

