    musicMap = NetEaseConfig(musicUrl, '', 'GET')
    lysicUrl = '/api/song/lyric?os=osx&id=' + mid + '&lv=-1&kv=-1&tv=-1'
    lysicMap = NetEaseConfig(lysicUrl, '', 'GET')
    if (musicMap.data[0].url) {
        return JSON.stringify({
