const getTencentPlayer = (mid) => {
    try {
        nameMap = ['标准', '高清', 'HD高清', '无损']
        musicMap = ['size_128mp3', 'size_192aac', 'size_320mp3', 'size_flac']
        musics = []
        i = musicMap.length
        while (i--) {
            body = 'https://api.dog886.com/v1/qq/getQQSong?id=' + mid + '&code=' + musicMap[i]
            music = JSON.parse(request(body)).data.url
            if (music.search('guid') >= 1) {
                musics.push(music)
            } else {
                nameMap.splice(i, 1)
            }
        }
        names = []
        x = nameMap.length
        while (x--) {
            names.push(nameMap[x])
        }
        body = 'https://api.dog886.com/v1/qq/getQQLyric?id=' + mid
        lysic = JSON.parse(request(body)).data
        if (musics.length) {
            return JSON.stringify({
                urls: musics,
                names: names,
                lyric: lysic
            })
        }
        return 'toast://暂无资源可播！！'
    } catch (e) {
        return 'toast://糟糕崩溃了！！请等待治愈....'
    }
}

const getKwmusiccarPlayer = (mid) => {
    let lyric = KwmusiccarConfig('/info/' + mid).lrc
    const getTime = (time) => {
        let h = parseInt(time / 60 / 60 % 24)
        h = h < 10 ? '0' + h : h
        let m = parseInt(time / 60 % 60)
        m = m < 10 ? '0' + m : m
        let s = parseInt(time % 60)
        s = s < 10 ? '0' + s : s
        return [m + ':' + s + '.00']
    }
    let api = 'http://antiserver.kuwo.cn/anti.s?type=convert_url3&format=mp3&response=url&rid='
    names = ['320k', '192k', '128k']
    let urls = []
    names.forEach(item => {
        urls.push({
            url: api + mid + '&br=' + item + 'mp3'
        })
    })
    urls = bf(urls).map(data => JSON.parse(data).url)
    lyric = lyric.map(i => '[' + getTime(i.time) + ']' + i.lineLyric).join('\r\n')
    return JSON.stringify({
        urls: urls,
        names: names,
        lyric: lyric
    })
}

